import React, { useState } from 'react';
import '../styles/FormStyles.css';

function Form() {
  const [cancelValue, setCancelValue] = useState({
    input: '',
  });

  const handleCancelClick = () => {
    setCancelValue({
      input: '',
    });
  };

  const handleInputChange = (event) => {
    const { input } = event.target;
    setCancelValue({ ...cancelValue, [input]: cancelValue.input });
  };

  return (
    <div className="form-container">
      <h2>Form</h2>
      <div className="inputs">
        <div className="input">
          <label>Input 1</label>
          <input type="text" onBlur={handleCancelClick} />
        </div>

        <div className="input">
          <label>Input 2</label>
          <input />
        </div>
      </div>
      <div className="buttons">
        <div className="button">
          <button onClick={() => setCancelValue('')}>Cancel</button>
        </div>
        <div className="button">
          <button>Save</button>
        </div>
      </div>
    </div>
  );
}

export default Form;
