import React, { useState } from 'react';
import '../styles/FormStyles.css';

function Form() {
  const [, setCancelValue] = useState({
    input: '',
  });

  function handleInputBlur(event) {
    setCancelValue(event.target.value);
  }

  return (
    <div className="form-container">
      <h2>Form</h2>
      <div className="inputs">
        <div className="input">
          <label>Input 1</label>
          <input type="text" onBlur={handleInputBlur} />
        </div>

        <div className="input">
          <label>Input 2</label>
          <input />
        </div>
      </div>
      <div className="buttons">
        <div className="button">
          <button onClick={() => setCancelValue('')}>Cancel</button>
        </div>
        <div className="button">
          <button>Save</button>
        </div>
      </div>
    </div>
  );
}

export default Form;
