import React, { useState } from 'react';
import '../styles/TableStyles.css';

function Table() {
  const [showForm, setShowForm] = useState(false);
  const [formValues, setFormValues] = useState({
    column1: '',
    column2: '',
    column3: '',
  });

  const [tableData, setTableData] = useState([
    {
      input1: 'Column 1, Row 1',
      input2: 'Column 2, Row 1',
      input3: 'Column 3, Row 1',
    },
    {
      input1: 'Column 1, Row 2',
      input2: 'Column 2, Row 2',
      input3: 'Column 3, Row 2',
    },
    {
      input1: 'Column 1, Row 3',
      input2: 'Column 2, Row 3',
      input3: 'Column 3, Row 3',
    },
  ]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleCancelClick = () => {
    setFormValues({
      column1: '',
      column2: '',
      column3: '',
    });
    setShowForm(false);
  };

  const handleSaveClick = () => {
    const { column1, column2, column3 } = formValues;
    const newData = {
      input1: column1,
      input2: column2,
      input3: column3,
    };
    setShowForm(false);
  };

  return (
    <div className="table-container">
      <div className="table">
        <h2>Table</h2>
        <table>
          <thead>
            <tr>
              <th>Column 1</th>
              <th>Column 2</th>
              <th>Column 3</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Column 1, Row 1</td>
              <td>Column 2, Row 1</td>
              <td>Column 3, Row 1</td>
            </tr>
            <tr>
              <td>Column 1, Row 2</td>
              <td>Column 2, Row 2</td>
              <td>Column 3, Row 2</td>
            </tr>
            <tr>
              <td>Column 1, Row 3</td>
              <td>Column 2, Row 3</td>
              <td>Column 3, Row 3</td>
            </tr>
            {tableData.map((row, index) => (
              <tr key={index}>
                <td>{row.column1}</td>
                <td>{row.column2}</td>
                <td>{row.column3}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showForm ? (
        <div className="ani-form">
          <h2>Form</h2>
          <div className="inputs">
            <div className="input">
              <label>Input - Column 1</label>
              <input
                name="column1"
                onChange={handleInputChange}
                value={formValues.column1}
              />
            </div>
            <div className="input">
              <label>Input - Column 2</label>
              <input
                name="column2"
                onChange={handleInputChange}
                value={formValues.column2}
              />
            </div>
            <div className="input">
              <label>Input - Column 3</label>
              <input
                name="column3"
                onChange={handleInputChange}
                value={formValues.column3}
              />
            </div>
          </div>
          <div className="buttons">
            <div className="button">
              <button onClick={() => setShowForm(false)}>Cancel</button>
            </div>
            <div className="button">
              <button>Save</button>
            </div>
          </div>
        </div>
      ) : (
        <button onClick={() => setShowForm(true)}>Add New Item</button>
      )}
    </div>
  );
}

export default Table;
