import { useState } from 'react';

function Form() {
  const [, setShowForm] = useState(false);

  const [formValues, setFormValues] = useState({
    column1: '',
    column2: '',
    column3: '',
  });

  const [tableData, setTableData] = useState([
    {
      input1: 'Column 1, Row 1',
      input2: 'Column 2, Row 1',
      input3: 'Column 3, Row 1',
    },
    {
      input1: 'Column 1, Row 2',
      input2: 'Column 2, Row 2',
      input3: 'Column 3, Row 2',
    },
    {
      input1: 'Column 1, Row 3',
      input2: 'Column 2, Row 3',
      input3: 'Column 3, Row 3',
    },
  ]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSaveClick = () => {
    const { column1, column2, column3 } = formValues;
    const newData = {
      input1: column1,
      input2: column2,
      input3: column3,
    };
    setTableData([...tableData, newData]);
    setFormValues({
      column1: '',
      column2: '',
      column3: '',
    });
    setShowForm(false);
  };

  return (
    <div className="ani-form">
      <h2>Form</h2>
      <div className="inputs">
        <div className="input">
          <label>Input - Column 1</label>
          <input
            name="column1"
            onChange={handleInputChange}
            value={formValues.column1}
          />
        </div>
        <div className="input">
          <label>Input - Column 2</label>
          <input
            name="column2"
            onChange={handleInputChange}
            value={formValues.column2}
          />
        </div>
        <div className="input">
          <label>Input - Column 3</label>
          <input
            name="column3"
            onChange={handleInputChange}
            value={formValues.column3}
          />
        </div>
      </div>
      <div className="buttons">
        <div className="button">
          <button onClick={() => setShowForm(false)}>Cancel</button>
        </div>
        <div className="button">
          <button onClick={handleSaveClick}>Save</button>
        </div>
      </div>
    </div>
  );
}

export default Form;
